urlset:
{cfg 'general_domain', '', false, 'cfg_'} 


	{$cfg_general_domain}
	{$tenant->modif_dtime}
	weekly
	0.6

{if $tenant->domain}

	{$tenant->domain}
	{$tenant->modif_dtime}
	weekly
	0.6

{/if}

	{$tenant->subdomain}.{$cfg_general_domain}/
	{$tenant->modif_dtime}
	weekly
	0.6

{foreach $spaList as $spa}
	{if $tenant->domain}

			{$tenant->domain}/{$spa->name}
			{$spa->modif_dtime}
			weekly
			0.5

	{/if}

		{$tenant->subdomain}.{$cfg_general_domain}/{$spa->name}@/}
		{$spa->modif_dtime}
		weekly
		0.5

{/foreach}
