urlset:
{cfg 'general_domain', '', false, 'cfg_'}
{cfg 'general_protocol', 'http', false, 'cfg_'}

	{$cfg_general_protocol}://{$cfg_general_domain}
	{$tenant->modif_dtime}
	weekly
	0.6

{if $tenant->domain}

	{$cfg_general_protocol}://{$tenant->domain}
	{$tenant->modif_dtime}
	weekly
	0.6

{/if}

	{$cfg_general_protocol}://{$tenant->subdomain}.{$cfg_general_domain}/
	{$tenant->modif_dtime}
	weekly
	0.6

{foreach $spaList as $spa}
	{if $tenant->domain}

			{$cfg_general_protocol}://{$tenant->domain}/{$spa->name}
			{$spa->modif_dtime}
			weekly
			0.5

	{/if}

		{$cfg_general_protocol}://{$tenant->subdomain}.{$cfg_general_domain}/{$spa->name}@/}
		{$spa->modif_dtime}
		weekly
		0.5

{/foreach}
